var exec = require('child_process').exec;

module.exports = function(comando){
	return {
		abrir: function(){
			var cmd = "\"" + comando + "\"";
			console.log(cmd);
			exec(cmd, function(error, stdout, stderr){
				console.log(stdout);
				console.log(error);
				console.log(stderr);
			});
		},
		executar: function(){
			console.log(comando);
			exec(comando, function(error, stdout, stderr){
				console.log(stdout);
				console.log(error);
				console.log(stderr);
			});
		},
	}
}

function replaceAll(str, de, para){
    var pos = str.indexOf(de);
    while (pos > -1){
		str = str.replace(de, para);
		pos = str.indexOf(de);
	}
    return (str);
}